import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const Summary = ({ route }) => {
  const { questions } = route.params;
  let totalScore = 0;

  return (
    <View style={styles.container}>
      <Text style={styles.totalScore} testID="total">Total Score: {totalScore}</Text>
      {questions.map((question, index) => {
        const isCorrect = question.type === 'true-false'
          ? question.correct === 0 && question.correct === index
          : Array.isArray(question.correct)
            ? question.correct.every((answerIndex) => answerIndex === index)
            : question.correct === index;
        if (isCorrect) totalScore++;

        return (
          <View key={index} style={styles.questionContainer}>
            <Text style={styles.question}>{question.prompt}</Text>
            <Text style={isCorrect ? styles.correctAnswer : styles.incorrectAnswer}>
              Your Answer: {question.choices[index]}
            </Text>
            <Text style={styles.correctAnswer}>
              Correct Answer: {question.choices[question.correct]}
            </Text>
          </View>
        );
      })}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  totalScore: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  questionContainer: {
    marginBottom: 20,
  },
  question: {
    marginBottom: 10,
  },
  correctAnswer: {
    color: 'green',
  },
  incorrectAnswer: {
    color: 'red',
    textDecorationLine: 'line-through',
  },
});

export default Summary;
